import React, { Component, useState } from 'react';
import { BrowserRouter as Router, Link, Route } from 'react-router-dom';


import Grid from '@material-ui/core/Grid'


import { makeStyles } from '@material-ui/styles';

import { Typography } from '@material-ui/core';



const useStyles = makeStyles(theme => ({
  dialogTitle:{
    display:"flex",
    justifyContent:"space-between",
    alignItems:"center"
  },
  icon:{
    float:"right"
  },
  returningCustomerText:{
    marginTop:25,
  },
  signInButton:{
    height:47,
    width:80,
    margin:0,
    color: '#FFFFFF',
    backgroundColor: '#00B4F4',
    borderRadius:0,
    '&:hover':{
      backgroundColor: '#D6D6D6'
    }
  },
  errorPaper:{
    borderRadius:0,
    backgroundColor: '#FFBDBD',
    height:70
  },
  errorText:{
    align:"center"
  },
  link:{
    color:"#000000",
    textDecoration: "none",
  },
  footer:{
    width:`calc(100% - 225px)`,
    marginLeft:225,
    height:200,
    backgroundColor:"#EBEBEB",
    textAlign: 'center',
  },
  footerTop:{
    width:`calc(100% - 225px)`,
    marginLeft:225,
    height:200,
    color:"#FFFFFF",
    backgroundColor:"#2C2A2B",
    justify:"center",
    align:"center",
    textAlign: 'center',
  },

}));



const Footer = (props) => {

  const classes = useStyles();

  return(
    <React.Fragment>

    <Grid container className={classes.footerTop} alignItems="center">
      <Grid item xs={12} justify> 
        <Typography variant="h2"> Best For Rest  </Typography> 
      </Grid>

    </Grid>

    <Grid container className={classes.footer} alignItems="center">
      <Grid item xs={3} className={classes.footerContent}>  
        <Typography> 39 Morpeth Road  </Typography>
        <Typography> Victoria Park  </Typography>
        <Typography> London E9 7LD </Typography>
        <Typography> +44 (0)242 5123-4212 </Typography>
        <Typography> sales@hause.com</Typography>
      </Grid>
      <Grid item xs={3}>  
      <Typography> 
            <Link to ={'/contact'} className={classes.link}> 
            About us
            </Link>  
          </Typography>
        <Typography> 
          <Link to = {'/products'} className={classes.link}> 
          Products
          </Link>  
        </Typography>
      </Grid>
      <Grid item xs={3}>  
        <Typography> 
            <Link to ={'/register'} className={classes.link}> 
            Register 
            </Link>  
          </Typography>
        <Typography> 
          <Link to = {'/login'} className={classes.link}> 
          Login 
          </Link>  
        </Typography>
      </Grid>
    </Grid>

    </React.Fragment>
  );

}


export default Footer;
